import { View, Text, Platform, SafeAreaView, StyleSheet } from 'react-native';
import React, { useRef, useState, useEffect } from 'react';
import { KeyboardAwareScrollView } from 'react-native-keyboard-aware-scroll-view';

import { Formik } from 'formik';
import * as yup from 'yup';

import { COLORS, SIZES, FONTS } from '../../constants';

import { TextField } from "../../components/TextField";
import SelectField from "../../components/SelectField";
import { ButtonWithTitle } from "../../components/ButtonWithTitle";
import ErrorSheet from "../../components/ErrorSheet";
import { PharmaListItem } from "../../components/PharmaListItem";
import ContentSheet from "../../components/ContentSheet";

import axios from 'axios';
import { BASE_URL } from '../../utils';

import Wizard, { WizardRef } from 'react-native-wizard';

import {
    NativeBaseProvider,
    extendTheme,
    Image
} from "native-base";
// Define the config
const config = {
    useSystemColorMode: false,
    initialColorMode: "light",
};

// extend the theme
export const theme = extendTheme({
    colors: {
        primary: {
            100: '#52A56E',
            200: '#C6C6C6'
        }
    },
    config
});
type MyThemeType = typeof theme;
declare module "native-base" {
    interface ICustomTheme extends MyThemeType { }
}


interface PharmaModel {
    pharmacyCode: string;
    pharmacyName: string;
    state: string;
    lga: string;
    address: string;
}

const FindPharmaScreen = () => {

    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [hasError, setHasError] = useState(false);



    const wizard = useRef<WizardRef>(null);

    const [isFirstStep, setIsFirstStep] = useState(true);
    const [isLastStep, setIsLastStep] = useState(false);
    const [currentStep, setCurrentStep] = useState(0);

    const keyboardVerticalOffset = Platform.OS === 'ios' ? 40 : 0;

    const [addressState, setAddressState] = useState('');
    const [pharmas, setPharmas] = useState<Array<PharmaModel>>([]);



    const states = [
        { title: "Abia", value: "Abia" },
        { title: "Adamawa", value: "Adamawa" },
        { title: "Akwa Ibom", value: "Akwa Ibom" },
        { title: "Anambra", value: "Anambra" },
        { title: "Bauchi", value: "Bauchi" },
        { title: "Bayelsa", value: "Bayelsa" },
        { title: "Benue", value: "Benue" },
        { title: "Borno", value: "Borno" },
        { title: "Cross River", value: "Cross River" },
        { title: "Delta", value: "Delta" },
        { title: "Ebonyi", value: "Ebonyi" },
        { title: "Edo", value: "Edo" },
        { title: "Ekiti", value: "Ekiti" },
        { title: "Enugu", value: "Enugu" },
        { title: "FCT - Abuja", value: "FCT - Abuja" },
        { title: "Gombe", value: "Gombe" },
        { title: "Imo", value: "Imo" },
        { title: "Jigawa", value: "Jigawa" },
        { title: "Kaduna", value: "Kaduna" },
        { title: "Kano", value: "Kano" },
        { title: "Katsina", value: "Katsina" },
        { title: "Kebbi", value: "Kebbi" },
        { title: "Kogi", value: "Kogi" },
        { title: "Kwara", value: "Kwara" },
        { title: "Lagos", value: "Lagos" },
        { title: "Nasarawa", value: "Nasarawa" },
        { title: "Niger", value: "Niger" },
        { title: "Ogun", value: "Ogun" },
        { title: "Ondo", value: "Ondo" },
        { title: "Osun", value: "Osun" },
        { title: "Oyo", value: "Oyo" },
        { title: "Plateau", value: "Plateau" },
        { title: "Rivers", value: "Rivers" },
        { title: "Sokoto", value: "Sokoto" },
        { title: "Taraba", value: "Taraba" },
        { title: "Yobe", value: "Yobe" },
        { title: "Zamfara", value: "Zamfara" },

    ];
    const LocationSchema = yup.object().shape({
        state: yup.string().required('State is required'),
    });


    const clearError = () => {
        setHasError(false);
        setLoading(false);
    };
    const findPharmas = async () => {
        try {
            setLoading(true);
            const configurationObject = {
                method: 'post',
                url: `${BASE_URL}pharmacies`,
                data: {
                    state: addressState
                }
            };
            const response = await axios(configurationObject);

            let resBody = response.data;

            if (resBody['status'] === 'error') {
                setLoading(false);
                setError("Error loading pharmas");
                setHasError(true);
                return;
            } else {
                setPharmas(resBody.pharmacies);
                setLoading(false);
                return;

            }
        } catch (error) {
            setLoading(false);
            setError(error.message);
            setHasError(true);
            return;
        }
    };
    const handleMain = async () => {
        await findPharmas();
        wizard.current!.next();
    };



    const searchingFunc = (obj, value) => {
        for (let key in obj) {
            if (
                !Number.isInteger(obj[key]) &&
                !(typeof obj[key] === "object") &&
                !(typeof obj[key] === "boolean")
            ) {
                if (obj[key].toLowerCase().indexOf(value.toLowerCase()) != -1) {
                    return true;
                }
            }
        }
        return false;
    };


    const Main = ({ handleSubmit }) => {
        return (
            <View style={{ ...styles.stepContainer }}>
                <View style={{ flex: 2, width: '100%', justifyContent: 'center', alignItems: 'center' }}>
                    <Image source={require("../../../assets/location.png")} alt={'Search Image'} style={{ resizeMode: 'contain', marginBottom: 0 }} w={'2/5'} />
                </View>

                <View style={styles.formContainer}>
                    <Text style={{ ...styles.headingText }}>Find Pharmacies</Text>
                    <Text style={{ ...styles.subHeadingText }}>
                        Search SOSOCARE partner Pharmacies to access Insurance
                    </Text>
                    <View style={styles.form}>
                        <Formik

                            initialValues={{
                                state: addressState,
                            }}
                            onSubmit={values => {
                                handleSubmit(values);
                            }}
                            validationSchema={LocationSchema}
                        >
                            {({ errors, handleBlur, touched, handleChange, handleSubmit }) => (
                                <View style={{ width: '100%' }}>
                                    <SelectField
                                        hasIcon={false}
                                        label='State'
                                        handleChange={(val) => {
                                            handleChange('state')(val);
                                            setAddressState(val);
                                        }}
                                        options={states}
                                        value={addressState}
                                        isDisabled={loading}
                                    />
                                    {errors.state && touched.state ? <Text style={styles.errorText}>{errors.state}</Text> : null}
                                    <ButtonWithTitle loading={loading} noBg={false} title={'Search'} backgroundColor={COLORS.primary} color={COLORS.white} onTap={handleSubmit} width={'100%'} />
                                </View>
                            )}
                        </Formik>
                    </View>
                </View>
            </View>
        );
    };
    const Results = () => {
        const [searchText, setSearchText] = useState('');
        const [displayPharmas, setDisplayPharmas] = useState<Array<PharmaModel>>([]);
        const [activePharma, setActivePharma] = useState<PharmaModel>();
        const [showContent, setShowContent] = useState(false);
        const closeContent = () => {
            setShowContent(false);
        };
        const viewPharmacy = async(code) => {
            try {
                let currentItem = displayPharmas.find((item) => item.pharmacyCode === code);
                if (currentItem) {
                    setActivePharma(currentItem);
                    setShowContent(true);
                }
            } catch (error) {
                setError("Error kindly retry");
                setHasError(true);
            }
        };

        useEffect(() => {
            let mounted = true;
            (async () => {
                if (!mounted) {
                    return;
                }
                let group = pharmas;
                if (searchText && searchText.trim() !== "") {
                    let results: Array<PharmaModel> = [];
                    for (let j = 0; j < group.length; j++) {
                        if (searchingFunc(group[j], searchText)) {
                            results.push(group[j]);
                        }
                    }
                    setDisplayPharmas(results);
                } else {
                    setDisplayPharmas(pharmas);
                }
            })();
            return () => {
                mounted = false;
            };
        }, [searchText]);
        const PharmaContent = () => {
            const activeItem = activePharma;
            if (activeItem) {
                return (
                    <View style={{ width: '100%', justifyContent: 'flex-start', alignItems: 'flex-start', paddingHorizontal: 12 }}>
                        <Text style={{ ...styles.transactionHeading, color: COLORS.pallete_deep }}>Pharmacy Details</Text>
                        <View style={{ width: '100%', justifyContent: 'flex-start', alignItems: 'flex-start', marginTop: 20 }}>
                            <View style={styles.transactionRecord}>
                                <Text style={styles.transactionRecordLeft}>
                                    Name
                                </Text>
                                <Text style={styles.transactionRecordRight}>{activeItem.pharmacyName}</Text>
                            </View>
                            <View style={styles.transactionRecord}>
                                <Text style={styles.transactionRecordLeft}>
                                    Address
                                </Text>
                                <Text style={styles.transactionRecordRight}>{activeItem.address}</Text>
                            </View>
                        </View>
                    </View>
                );
            }
            return (<></>);

        };
        return (
            <View style={{ flex: 1 }}>
                <View style={{ width: '100%' }}>
                    <TextField
                        handleChange={(val) => setSearchText(val)}
                        label='Search pharmacy name or address'
                        icon={'search'}
                        isDisabled={loading}
                        hasIcon
                    />
                </View>
                <Text style={{ ...styles.insuranceHeading, color: COLORS.pallete_deep }}>Results</Text>
                <View style={{ width: '100%', flexDirection: 'column', justifyContent: 'center' }}>
                    <View style={{ width: '100%', marginTop: 0, marginBottom: 60 }}>
                        {
                            displayPharmas.length > 0 ?
                                displayPharmas.map((item: PharmaModel, index) => (
                                    <PharmaListItem
                                        backgroundColor={COLORS.pallete_white}
                                        tint={COLORS.fade}
                                        color={COLORS.dark}
                                        key={index}
                                        code={item.pharmacyCode}
                                        onTap={() => viewPharmacy(item.pharmacyCode)}
                                        name={item.pharmacyName}
                                        address={item.address}
                                    />
                                ))
                                : <Text style={{ fontSize: 16, marginVertical: 60, fontFamily: FONTS.medium, color: COLORS.pallete_deep, textAlign: 'center' }}>No Pharmacy found</Text>
                        }
                        <ButtonWithTitle
                            title='Go Back'
                            backgroundColor={COLORS.pallete_deep}
                            color={COLORS.white}
                            loading={loading}
                            onTap={() => wizard.current!.prev()}
                            width={'100%'}
                        />
                    </View>
                </View>
                {<ContentSheet content={<PharmaContent />} open={showContent} closed={closeContent} />}
            </View>
        );

    };

    const stepList = [
        {
            content: <Main handleSubmit={() => handleMain()} />,
        },
        {
            content: <Results />,
        },
    ];



    return (
        <NativeBaseProvider theme={theme}>
            <SafeAreaView style={{ backgroundColor: '#fefefe' }} />
            <KeyboardAwareScrollView extraScrollHeight={keyboardVerticalOffset} style={{ flex: 1, width: '100%', backgroundColor: 'white' }}>
                <View style={{ width: '100%', height: '100%', flex: 1, justifyContent: 'center', alignItems: 'center' }} >
                    <View style={styles.container}>
                        <View style={{ width: '100%' }}>
                            <Wizard
                                ref={wizard}
                                steps={stepList}
                                isFirstStep={val => setIsFirstStep(val)}
                                isLastStep={val => setIsLastStep(val)}
                                currentStep={({ currentStep, isLastStep, isFirstStep }) => {
                                    setCurrentStep(currentStep);
                                }}
                            />
                        </View>

                        <ErrorSheet error={error} open={hasError} closed={clearError} />
                    </View>
                </View>
            </KeyboardAwareScrollView>
            <SafeAreaView style={{ backgroundColor: '#fefefe' }} />
        </NativeBaseProvider>
    );
};


