import { View, Text, Pressable, SafeAreaView, ScrollView, StyleSheet } from 'react-native';
import React, { useState, useEffect, useContext } from 'react';

import { ButtonWithTitle } from "../../../components/ButtonWithTitle";
import ErrorSheet from "../../../components/ErrorSheet";
import SuccessSheet from "../../../components/SuccessSheet";
import { ListItem } from "../../../components/ListItem";
import Ionicons from '@expo/vector-icons/Ionicons';

import { COLORS, FONTS, SIZES } from '../../../constants';
import { BASE_URL } from '../../../utils';

import { connect } from 'react-redux';
import { ApplicationState, AgentState, OnAgentLogout, OnAgentUpdateProfileImage } from '../../../redux';

import * as ImagePicker from 'expo-image-picker';
import * as FileSystem from 'expo-file-system';

import { useNavigation } from "@react-navigation/native";
import { HomeScreenNavigationProp } from "../../../navigation/agent/types";
import { AuthContext } from "../../../contexts/AuthContext";
import {
  NativeBaseProvider,
  extendTheme,
  Image,
  Skeleton
} from "native-base";
// Define the config
const config = {
  useSystemColorMode: false,
  initialColorMode: "light",
};

// extend the theme
export const theme = extendTheme({
  colors: {
    primary: {
      100: '#52A56E',
      200: '#C6C6C6'
    }
  },
  config
});
type MyThemeType = typeof theme;
declare module "native-base" {
  interface ICustomTheme extends MyThemeType { }
}

interface ProfileScreenProps {
  agentReducer: AgentState;
  OnAgentLogout: Function;
  OnAgentUpdateProfileImage: Function;
}
const _ProfileScreen: React.FC<ProfileScreenProps> = (props) => {
  const { agentReducer, OnAgentLogout, OnAgentUpdateProfileImage } = props;
  const navigation = useNavigation<HomeScreenNavigationProp>();

  const { logout } = useContext(AuthContext);
  const logUserOut = async () => {
    try {
      let res = await OnAgentLogout();
      if (res.error) {
        setError(res.error);
        setHasError(true);
      }
      if (res.success) {
        logout();
      }

    } catch (e) {
      setError(e.message);
      setHasError(true);
    }
    logout();
  };

  const [imageUrl, setImageUrl] = useState('');
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [loading, setLoading] = useState(false);
  const [imageLoading, setImageLoading] = useState(false);
  const [error, setError] = useState('');
  const [hasError, setHasError] = useState(false);
  const [successMessage, setSuccessMessage] = useState('');
  const [isSuccess, setIsSuccess] = useState(false);

  const clearError = () => {
    setHasError(false);
  };
  const handleSuccess = () => {
    setIsSuccess(false);
  };

  const openImagePickerAsync = async () => {

    try {
      let permissionResult = await ImagePicker.requestMediaLibraryPermissionsAsync();
      if (permissionResult.granted === false) {
        alert("Permission to access camera roll is required!");
        return;
      }
      let pickerResult = await ImagePicker.launchImageLibraryAsync();
      if (pickerResult.cancelled) {
        return;
      }
      setLoading(true);
      let token = agentReducer.agent.authToken;
      if (!token) return;
      if (!pickerResult.cancelled) {

        let localUri = pickerResult.uri;
        let filename = localUri.split('/').pop();
        let fileType = localUri.substring(localUri.lastIndexOf(":") + 1, localUri.lastIndexOf(";")).split("/").pop();

        const res = await FileSystem.uploadAsync(`${BASE_URL}agent/upload-profile-image`, pickerResult.uri, {
          httpMethod: 'POST',
          uploadType: FileSystem.FileSystemUploadType.MULTIPART,
          fieldName: 'image',
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });

        let out = JSON.parse(res.body);
        if (out.status === 'error') {
          setError(out.message);
          setLoading(false);
          setHasError(true);
        }
        if (out.status === 'success') {
          setLoading(false);
          setSuccessMessage("Image uploaded");
          setImageUrl(out.image);
          setImageLoading(true);
          await OnAgentUpdateProfileImage(out.image);
          setIsSuccess(true);
        }
      }
    } catch (error) {
      if (error.message === "JSON Parse error: Unrecognized token '<'") {
        setError("File too large");
        setHasError(true);
        setLoading(false);
      } else {
        setError(error.message);
        setHasError(true);
        setLoading(false);
      }
    }

  };

  useEffect(() => {
    let mounted = true;
    (async () => {
      if (!mounted) {
        return;
      }
      try {
        setLoading(true);
        if (!mounted) {
          return;
        }
        let existingAgent = agentReducer.agent;
        let existingImage = existingAgent.image;
        setImageUrl(existingImage || '');
        setFirstName(existingAgent.firstName);
        setLastName(existingAgent.lastName);
        if (existingImage) setImageLoading(true);
        setLoading(false);
      } catch (error) {
        if (!mounted) {
          return;
        }
        setLoading(false);
        return;
      }

    })();
    return () => {
      mounted = false;
    };
  }, []);

  return (
    <NativeBaseProvider theme={theme}>
      <SafeAreaView style={{ backgroundColor: 'white' }} />
      <ScrollView keyboardDismissMode='interactive' style={{ width: '100%', height: '100%', flex: 1, backgroundColor: 'white' }} contentContainerStyle={styles.container}>
        <View style={styles.mainContainer}>
          <View style={{ flex: 1, width: '100%', justifyContent: 'center', alignItems: 'center' }}>
            <View style={{
              width: 120,
              height: 120,
              borderRadius: 60,
              borderColor: COLORS.primary,
              borderWidth: 2,
              padding: 3,
              marginBottom: 8,
              position: 'relative'
            }}>
              <Pressable
                disabled={loading || imageLoading}
                onPress={() => openImagePickerAsync()}
                style={({ pressed }) => ({
                  opacity: pressed ? 0.5 : 1,
                  width: '100%',
                  height: '100%',
                  flex: 1,
                  justifyContent: 'center',
                  alignItems: 'center'
                })}
              >
                {loading
                  ? <Skeleton rounded='full' w={'full'} h={'full'} />
                  : !loading && imageUrl
                    ? <View style={{ flex: 1, position: 'relative', justifyContent: 'center', alignItems: 'center', width: "100%", height: '100%' }}>
                      <Image alt='Profile Image'
                        onLoad={() => setImageLoading(false)} source={{ uri: imageUrl }}
                        style={{
                          width: '100%',
                          height: '100%',
                          flex: 1,
                          resizeMode: 'cover',
                          borderRadius: 60,
                        }}
                      />
                      <Skeleton position={'absolute'} top={0} zIndex={5} display={imageLoading ? 'flex' : 'none'} rounded='full' w={'full'} h={'full'} />
                    </View>
                    : <Image alt='Profile Image' source={require("../../../../assets/blank.png")}
                      style={{
                        width: '100%',
                        height: '100%',
                        flex: 1,
                        resizeMode: 'cover',
                        borderRadius: 60,
                      }}
                    />
                }

              </Pressable>
              <Pressable
                disabled={loading || imageLoading}
                onPress={() => openImagePickerAsync()}
                style={({ pressed }) => ({
                  opacity: pressed ? 0.5 : loading ? 0.7 : 1,
                  // backgroundColor: pressed ? tint: backgroundColor,
                  position: 'absolute', justifyContent: 'center', alignItems: 'center', right: 3, bottom: 0, backgroundColor: COLORS.primary, width: 40, height: 40, borderRadius: 20, padding: 3
                })}
              >
                <Ionicons name={'ios-camera-outline'} style={{ fontSize: 24 }} color={'white'} />
              </Pressable>
            </View>
            <Text style={{ textAlign: 'center', marginBottom: 20, fontSize: SIZES.large, fontFamily: FONTS.semiBold, color: COLORS.dark }}>{firstName} {lastName}</Text>
            <View style={{ width: '40%', justifyContent: 'center', alignItems: 'center' }}>
              <ButtonWithTitle loading={false} title={'Edit Profile'} size={'sm'} backgroundColor={COLORS.primary} onTap={() => navigation.navigate("EditProfile")} bordered={false} width={'100%'} color={'white'} noBg={false} />
            </View>
          </View>
          <View style={{ flex: 2, width: '100%', marginTop: 30, justifyContent: 'flex-start', alignItems: 'center', flexDirection: 'column' }}>
            <View style={{ ...styles.section }}>
              <ListItem
                title='Settings'
                backgroundColor='white'
                color={COLORS.dark}
                tint={COLORS.fade}
                loading={false}
                onTap={() => navigation.navigate('EditProfile')}
                width={'100%'}
                leftIcon={'ios-settings-outline'}
              />
              <ListItem
                title='Location'
                backgroundColor='white'
