import { View, Text, ScrollView, SafeAreaView, StyleSheet, RefreshControl, Image, Dimensions } from 'react-native';
import React, { useRef, useCallback, useEffect, useState } from 'react';
import { formatCurrency } from "react-native-format-currency";
import Carousel from 'react-native-snap-carousel';

import { COLORS, SIZES, FONTS } from '../../../constants';

import { ButtonWithTitle } from "../../../components/ButtonWithTitle";
import { InsuranceListItem } from "../../../components/InsuranceListItem";
import NoticeSheet from "../../../components/NoticeSheet";
import ErrorSheet from "../../../components/ErrorSheet";
import SuccessSheet from "../../../components/SuccessSheet";
import ContentSheet from "../../../components/ContentSheet";
import axios from 'axios';
import { BASE_URL } from '../../../utils';


import { useNavigation } from "@react-navigation/native";
import { ClientDetailsScreenRouteProp, ClientScreenNavigationProp } from "../../../navigation/agent/types";
import { useRoute } from '@react-navigation/native';

import { connect } from 'react-redux';
import { ApplicationState, AgentState, PlanModel, InsuranceModel, OnLoadAgentClient} from '../../../redux';

import {
  NativeBaseProvider,
  extendTheme
} from "native-base";
// Define the config
const config = {
  useSystemColorMode: false,
  initialColorMode: "light",
};
// extend the theme
export const theme = extendTheme({
  colors: {
    primary: {
      100: '#52A56E',
      200: '#C6C6C6'
    }
  },
  config
});
type MyThemeType = typeof theme;
declare module "native-base" {
  interface ICustomTheme extends MyThemeType { }
}

interface BuyInsuranceProps {
  agentReducer: AgentState;
  OnLoadAgentClient: Function;
}

const wait = (timeout: number) => {
  return new Promise(resolve => setTimeout(resolve, timeout));
};
const deviceWidth = Dimensions.get('screen').width;
const _BuyInsurance: React.FC<BuyInsuranceProps> = (props) => {
  const { agentReducer, OnLoadAgentClient } = props;
  const route = useRoute<ClientDetailsScreenRouteProp>();
  const { client } = route.params;
  const navigation = useNavigation<ClientScreenNavigationProp>();

  const [inLoading, setInLoading] = useState(false);
  const [loading, setLoading] = useState(false);
  const [reload, setReload] = useState(false);

  const [insurance, setInsurance] = useState<InsuranceModel>();
  const [plans, setPlans] = useState<Array<PlanModel>>();
  const [error, setError] = useState('');
  const [noticeMessage, setNoticeMessage] = useState('');
  const [hasError, setHasError] = useState(false);
  const [hasNotice, setHasNotice] = useState(false);

  const clearError = () => {
    setHasError(false);
  };
  useEffect(() => {
    let mounted = true;
    (async () => {
      try {
        if (!mounted) {
          return;
        }
        setInLoading(true);
        let res = await OnLoadAgentClient(client._id);
        if (res.success) {
          setInsurance(res.insurance);
          setInLoading(false);
        } else if (res.error) {
          setInLoading(false);
          setError(res.error);
          setHasError(true);
        }
        let plans = res.plans;
        if (!mounted) {
          return;
        }
        setPlans(plans);
        setLoading(false);
      } catch (error) {
        setLoading(false);
        setError(error.message);
        setHasError(true);
        return;
      }

    })();
    return () => {
      mounted = false;
    };
  }, [reload]);
  const [refreshing, setRefreshing] = useState(false);
  const onRefresh = useCallback(() => {
    let mounted = true;
    (async () => {
      if (!mounted) {
        return;
      }
      setRefreshing(true);
      setReload(prev => !prev);
      wait(2000).then(() => setRefreshing(false));
    })();

    return () => {
      mounted = false;
    };

  }, []);

  const openPlan = (id) => {
    const selectedPlan = plans!.find((item) => item._id === id);
    if (selectedPlan) {
      navigation.navigate('Plan', { plan: selectedPlan, client });
    }
  };

  return (
    <NativeBaseProvider theme={theme}>
      <SafeAreaView style={{ backgroundColor: '#fefefe' }} />
      <ScrollView
        refreshControl={
          <RefreshControl
            refreshing={refreshing}
            onRefresh={onRefresh}
            colors={[COLORS.primary]}
            tintColor={COLORS.primary}
          />
        }
        style={{ flex: 1, backgroundColor: 'white' }}
      >
        <View style={styles.container}>
          <InsuranceDetails insurance={insurance} client={client} />
          {plans &&
            <View style={{ ...styles.insuranceContainer, width: deviceWidth, borderRadius: 0, borderTopRightRadius: 0, borderTopLeftRadius: 0, borderWidth: 1, borderColor: COLORS.fade, backgroundColor: COLORS.white, marginBottom: 40 }}>

              <Text style={{ fontSize: 22, width: '100%', fontFamily: FONTS.semiBold, color: COLORS.dark, marginBottom: 10 }}>Insurance Plans</Text>

              <View style={{ marginVertical: 10, width: '100%', alignItems: 'center' }}>
                <InsurancePlans plans={plans} onNavigate={openPlan} />
              </View>
            </View>}
          <InsuranceHistory user={client} agent={agentReducer.agent} onViewHistory={() => { }} />
        </View>

      </ScrollView>
      <SafeAreaView style={{ backgroundColor: '#fefefe' }} />
    </NativeBaseProvider>
  );
};

interface DetailsProps {
  insurance: InsuranceModel | undefined | null;
  client: any;
}
const InsuranceDetails: React.FC<DetailsProps> = ({ insurance, client }) => {
  return (
    <View style={{ ...styles.insuranceContainer, borderWidth: 1, borderColor: COLORS.fade, backgroundColor: COLORS.pallete_deep }}>
      <Text style={styles.insuranceHeading}>Client {client.first_name} {client.last_name}'s Insurance status</Text>
      {insurance
        ? <View style={{ width: '100%', flexDirection: 'column', justifyContent: 'center' }}>
          <Text style={{ ...styles.insurancePlan, color: COLORS.pallete_cream }}>{insurance.plan}</Text>
          <View style={{ width: '100%', flexDirection: 'row', justifyContent: 'flex-start', alignItems: 'center', marginBottom: 8 }}>
            <Text style={{ fontSize: 16, flexShrink: 1, fontFamily: FONTS.medium, color: COLORS.white, marginRight: 3 }}>Insurance Code:</Text>
            <Text style={{ fontSize: 16, flexShrink: 1, fontFamily: FONTS.medium, color: COLORS.white }}>{insurance.insurance_provider_id}</Text>
          </View>

          <View style={{ width: '100%', flexDirection: 'row', justifyContent: 'flex-start', alignItems: 'center', marginBottom: 20 }}>
            <Text style={{ fontSize: 14, fontFamily: FONTS.medium, color: COLORS.white, marginRight: 3 }}>Expires:</Text>
            <Text style={{ fontSize: 14, fontFamily: FONTS.medium, color: COLORS.white }}>{new Date(insurance.expiry_date).toDateString()}</Text>
          </View>

        </View>
        : <View style={{ width: '100%', flexDirection: 'column', justifyContent: 'center', marginBottom: 20 }}>
          <Text style={{ width: '100%', textAlign: 'center', fontSize: 14, fontFamily: FONTS.semiBold, color: COLORS.pallete_cream }}>
            No active Insurance subscriptions. Select an Insurance plan Below to continue.
          </Text>
        </View>
      }
    </View>
  );
};

type MyProps = { plans: Array<PlanModel>, onNavigate: Function; };
type MyState = { activeIndex: number, carouselItems: Array<PlanModel>; };
export class InsurancePlans extends React.Component<MyProps, MyState> {

  constructor(props) {
    super(props);
    this.state = {
      activeIndex: 0,
      carouselItems: props.plans
    };
  }

  _renderItem = ({ item, index }) => {
    const backgroundImage = BackgroundImageService.GetImage(item.name);
    const [incomeValueFormattedWithSymbol, incomeValueFormattedWithoutSymbol, incomeSymbol] = formatCurrency({ amount: item.price / 100, code: "USD" });
    return (
      <View key={item._id} style={{ width: '100%', borderRadius: 15, overflow: 'hidden', borderWidth: 1, borderColor: item.name === 'Sosocare Gold Plan' ? COLORS.pallete_cream : COLORS.fade, marginVertical: 10, backgroundColor: item.name === 'Sosocare Gold Plan' ? COLORS.pallete_cream : item.name === 'Sosocare Silver Plan' ? COLORS.pallete_white : COLORS.white, }}>

        <Image source={backgroundImage} style={{ width: "100%", borderTopLeftRadius: 13, borderTopRightRadius: 8 }} />
        <View style={{ paddingHorizontal: 14, paddingVertical: 10 }}>
          <Text style={{ ...styles.insurancePlan, color: COLORS.pallete_deep, textTransform: 'uppercase', flexShrink: 1 }}>{item.ussd_name}</Text>
          <View style={{ flexDirection: 'row', justifyContent: 'space-between', alignItems: 'flex-end' }}>
            <Text style={{ fontSize: 18, fontFamily: FONTS.bold, color: COLORS.pallete_deep }}>Price: {'\u20A6'}{incomeValueFormattedWithoutSymbol}</Text>

          </View>
          <View style={{ flexDirection: 'row', justifyContent: 'space-between', alignItems: 'flex-end' }}>
            <Text style={{ fontSize: 18, fontFamily: FONTS.bold, color: COLORS.pallete_deep }}>Waste Cost: {item.waste_price/1000} {"Kg"}</Text>

          </View>
          <View style={{ flexDirection: 'row', marginTop: 24, justifyContent: 'space-between', alignItems: 'flex-end' }}>

            <ButtonWithTitle
              size='sm'
              title='View'
              width={'100%'}
              onTap={() => this.props.onNavigate(item._id)}
              backgroundColor={'transparent'}
              color={COLORS.pallete_deep}
              bordered
              loading={false}
            />

          </View>
        </View>
      </View>
    );
  };

  render() {
    return (
      <Carousel
        layout={"default"}
        vertical={false}
        // ref={ref => this.carousel = ref}
        data={this.state.carouselItems}
        renderItem={this._renderItem}
        sliderWidth={deviceWidth}
        itemWidth={deviceWidth * 0.8}

        onSnapToItem={index => this.setState({ activeIndex: index })}
      />
    );
  }
}


const InsuranceHistory = ({ onViewHistory, user, agent }) => {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [hasError, setHasError] = useState(false);
  const [history, setHistory] = useState<Array<InsuranceModel>>();
  const [showContent, setShowContent] = useState(false);
  const [activeItem, setActiveItem] = useState<InsuranceModel>();

  const closeContent = () => {
    setShowContent(false);
    setLoading(false);
  };

  const clearError = () => {
    setHasError(false);
    setLoading(false);
  };
  useEffect(() => {
    let mounted = true;
    const source = axios.CancelToken.source();
    (async () => {
      if (!mounted) {
        return;
      }
      try {
        setLoading(true);
        const response = await axios.get<string>(`${BASE_URL}agent/insurance/latest/${user._id}`, {
          cancelToken: source.token,
          headers: {
            'Authorization': `Bearer ${agent.authToken}`
          }
        });

        let resBody = response.data;

        if (resBody['status'] === 'error') {
          setLoading(false);
          setError('Unable to load transations');
          setHasError(true);
          return { 'error': resBody['message'] };
        } else {
          if (!mounted) {
            return;
          }
          setHistory(resBody['insurances']);
          setLoading(false);
          return { 'success': resBody['status'] };
        }
      } catch (error) {
        setLoading(false);
        setError('Unable to load Insurance History');
        setHasError(true);
        return;
      }

    })();
    return () => {
      source.cancel();
      mounted = false;
    };

  }, []);

  const showInsurance = async (id) => {
    if (history) {
      try {
        setLoading(true);
        let currentItem = history.find((item) => item._id === id);
        if (currentItem) {
          setActiveItem(currentItem);
          setShowContent(true);
        }
      } catch (error) {
        setLoading(false);
        setError("Error kindly retry");
        setHasError(true);
      }
    }
  };
  const InsuranceContent = () => {
    if (activeItem) {
      return (
        <View style={{ width: '100%', justifyContent: 'flex-start', alignItems: 'flex-start', paddingHorizontal: 12 }}>
          <Text style={{ ...styles.transactionHeading, color: COLORS.pallete_deep }}>Insurance Details</Text>
          <View style={{ width: '100%', justifyContent: 'flex-start', alignItems: 'flex-start', marginTop: 20 }}>
            <View style={styles.transactionRecord}>
              <Text style={styles.transactionRecordLeft}>
                Activation Date
              </Text>
              <Text style={styles.transactionRecordRight}>{new Date(activeItem.activation_date).toDateString()}</Text>
            </View>
            <View style={styles.transactionRecord}>
              <Text style={styles.transactionRecordLeft}>
                Expiry Date
              </Text>
              <Text style={styles.transactionRecordRight}>{new Date(activeItem.expiry_date).toDateString()}</Text>
            </View>
            <View style={styles.transactionRecord}>
              <Text style={styles.transactionRecordLeft}>
                Reference
              </Text>
              <Text style={styles.transactionRecordRight}>{activeItem.payment_ref}</Text>
            </View>
            <View style={styles.transactionRecord}>
              <Text style={styles.transactionRecordLeft}>
                Plan
              </Text>
              <Text style={{ ...styles.transactionRecordRight, textTransform: 'capitalize' }}>{activeItem.plan}</Text>
            </View>
            <View style={styles.transactionRecord}>
              <Text style={styles.transactionRecordLeft}>
                Status
              </Text>
              <View style={[
                styles.transactionRecordRight, { justifyContent: 'flex-start', alignItems: 'center', flexDirection: 'row' }]}
              >
                <Text style={[
                  { maxWidth: 50 },
                  activeItem.status === 'active' || activeItem.status === 'renewed'
                    ? { color: COLORS.pallete_white, justifyContent: 'center', alignItems: 'center', borderRadius: 10, fontSize: SIZES.base, backgroundColor: COLORS.pallete_deep, padding: 6 }
                    : activeItem.status === 'failed'
                      ? { color: COLORS.pallete_white, justifyContent: 'center', alignItems: 'center', borderRadius: 10, fontSize: SIZES.base, backgroundColor: COLORS.negative, padding: 6 }
                      : { color: COLORS.pallete_white, justifyContent: 'center', alignItems: 'center', borderRadius: 10, fontSize: SIZES.base, backgroundColor: COLORS.dark, padding: 6 }
                ]}
                >
                  {activeItem.status}
                </Text>
              </View>
            </View>
          </View>
        </View>
      );
    }
    return (<></>);

  };
  if (history?.length === undefined || history?.length < 1 ) {
    return (<></>);
  } else {

    return (
      <View style={{ ...styles.insuranceContainer, borderWidth: 1, borderColor: COLORS.fade, backgroundColor: COLORS.white, marginBottom: 100 }}>
        <Text style={{ ...styles.insuranceHeading, color: COLORS.pallete_deep }}>Insurance History</Text>
        <View style={{ width: '100%', flexDirection: 'column', justifyContent: 'center' }}>
          <View style={{ width: '100%', marginTop: 0 }}>
            {
              history && history.map((item: InsuranceModel) => (
                <InsuranceListItem
                  detail={item.plan}
                  date={item.activation_date}
                  plan={item.plan}
                  backgroundColor={COLORS.pallete_white}
                  tint={COLORS.white}
                  color={COLORS.dark}
                  loading={false}
                  key={item._id}
                  id={item._id}
                  onTap={showInsurance}
                />
              ))
            }
          </View>
          {<ContentSheet content={<InsuranceContent />} open={showContent} closed={closeContent} />}
          {/* <View style={{ marginTop: 20, width: '100%', flexDirection: 'row', justifyContent: 'space-between', alignItems: 'center' }}>
          <ButtonWithTitle
            title='View All'
            backgroundColor='transparent'
            noBg={false}
            size='sm'
            color={COLORS.dark}
            loading={false}
            onTap={() => { }}
            width={'100%'}
            bordered
          />
        </View> */}
        </View>
        <ErrorSheet error={error} open={hasError} closed={() => { }} />
      </View>
    );
  }
};

// IMAGE INTERFACE
interface BgImage {
